/*****************************************************************************
 *
 * FixedPrint_Setup - Setup the Blit Rectangles and load the XPM into the Font
 * Surface.  Should be called after SDL_Init, and before FixedPrint()
 *
 *****************************************************************************/
void FixedPrint_Setup()
  {
  int i;
  SDL_Color colors[2];
	SDL_Surface* temp;

	temp = IMG_ReadXPMFromArray(small_terminal_xpm);
	SDL_SetColorKey(temp, SDL_TRUE, SDL_MapRGB(temp->format, 0,0,0));
	FixedPrint_small_surface = SDL_CreateTextureFromSurface(renderer, temp);
	free(temp);
  
	temp = IMG_ReadXPMFromArray(mid_terminal_xpm);
	SDL_SetColorKey(temp, SDL_TRUE, SDL_MapRGB(temp->format, 0,0,0));
	FixedPrint_mid_surface = SDL_CreateTextureFromSurface(renderer, temp);
	free(temp);
  
	temp = IMG_ReadXPMFromArray(big_terminal_xpm);
	SDL_SetColorKey(temp, SDL_TRUE, SDL_MapRGB(temp->format, 0,0,0));
	FixedPrint_big_surface = SDL_CreateTextureFromSurface(renderer, temp);
	free(temp);
  
  for (i=0; i < 256; i++)
    {
    FixedPrint_small_lrect[i].x = (i % 16) * 6;
    FixedPrint_small_lrect[i].y = (i / 16) * 10;
    FixedPrint_small_lrect[i].w = 6;
    FixedPrint_small_lrect[i].h = 10;
    
    FixedPrint_mid_lrect[i].x = (i % 16) * 8;
    FixedPrint_mid_lrect[i].y = (i / 16) * 13;
    FixedPrint_mid_lrect[i].w = 8;
    FixedPrint_mid_lrect[i].h = 13;
    
    FixedPrint_big_lrect[i].x = (i % 16) * 10;
    FixedPrint_big_lrect[i].y = (i / 16) * 16;
    FixedPrint_big_lrect[i].w = 10;
    FixedPrint_big_lrect[i].h = 16;
    }
  }

