void SmallFixedPrint(SDL_Renderer* where, int x, int y, char* what, SDL_Color *fg)
  {
  char* ptr;
  SDL_Rect box;
  
  ptr = what;
  
  box.x = x;  box.y = y;
  box.w = 6;
  box.h = 10;
  
	SDL_SetTextureColorMod(FixedPrint_small_surface, fg->r, fg->g, fg->b);
  
  while (*ptr != '\0')
    {
		SDL_RenderCopy(where, FixedPrint_small_surface, 
								&FixedPrint_small_lrect[(unsigned char)*ptr], &box);
    box.x += 6;
    ptr++;
    }
  }

