void FixedPrint(SDL_Renderer* where, int x, int y, char* what, SDL_Color *fg, int size)
  {
  if (size == 0) SmallFixedPrint(where, x, y, what, fg);
  if (size == 1) MidFixedPrint(where, x, y, what, fg);
  if (size == 2) BigFixedPrint(where, x, y, what, fg);
  }
